import "./styles/ToMesh.css";
import React from "react";
import { BrowserView, MobileOnlyView } from "react-device-detect";
import Desktop_Title from "modules/toMesh/components/TMIntro";
import Header from "modules/toMesh/components/TMTitle";
import ButtonClick from "modules/toMesh/components/TMNotification";
import webpush from "web-push";

const ToMesh = () => {
  ButtonClick();

  if ("serviceWorker" in navigator) {
    navigator.serviceWorker.register("mockServiceWorker.js");
  }

  const keys = {
    publicKey:
      "BC5frPVyX5VqaUU-pwEbevfYjqBMymWollYD1ImZ4BTPbqsFnyN64dLCqwLAWtYjQ7b2DVS5yGLbVelgEIfdDyU",
    privateKey: "jkqRiTYV7Yqdnp8mDNP3DTo9hEbXgZ43mqY6YLsxiL4",
  };
  navigator.serviceWorker.ready.then((registration) => {
    registration.pushManager.getSubscription().then((subscription) => {
      if (subscription) {
        const endpoint = subscription.endpoint;
        const key = subscription.getKey("p256dh");
        const auth = subscription.getKey("auth");
        webpush.sendNotification(
          {
            endpoint: "http://localhost:3000/",
            keys: { p256dh: "", auth: "" },
          },
          JSON.stringify({ title: "Hello!", body: "World!" })
        );
      }
    });
  });

  return (
    <>
      <Header></Header>
      <BrowserView>
        <Desktop_Title />
      </BrowserView>
      <MobileOnlyView>
        <h1>
          <Desktop_Title />
        </h1>
      </MobileOnlyView>
    </>
  );
};

export default ToMesh;
