import { useState, useEffect } from "react";
import logo_image from "../../../assets/images/Mingle_AI_BI_v1_20231114_white.png";
import banner_image from "../../../assets/images/Banner.png";

function Header(props) {
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });

  return (
    <header>
      <h1>
        <img
          src={logo_image}
          alt="Title"
          style={{ width: 50 + innerWidth / 10 }}
          onClick={() => {}}
        />
      </h1>
    </header>
  );
}

function Banner(props) {
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });
  return (
    <header>
      <h1>
        <img
          src={banner_image}
          style={{ width: innerWidth / 2 }}
          alt="Title"
          onClick={() => {}}
        />
      </h1>
    </header>
  );
}

function TryButton(props) {
  return (
    <li>
      <input
        type="button"
        value="Try"
        style={{
          bottom: 10,
          inlineSize: props.innerWidth / 2 - 50,
        }}
        onClick={() => {}}
      />
    </li>
  );
}

function Launch_Desktop() {
  const [mode, setMode] = useState("WELCOME");
  const [id, setID] = useState(null);
  const [nextId, setNextId] = useState(4);
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });

  let content = null;
  let contextControl = null;

  return (
    <body bgcolor="black">
      <div className="App">
        <Header
          title="Title"
          onChangeMode={() => {
            setMode("WELCOME");
          }}
        ></Header>
        <div style={{ display: "flex" }}>
          <div style={{ width: innerWidth / 2 }}>
            <Banner></Banner>
          </div>
          <div style={{ width: innerWidth / 2 }}>
            Take a photo to create different styles of avatars!
            <TryButton innerWidth={innerWidth}></TryButton>
          </div>
        </div>
      </div>
    </body>
  );
}

export default Launch_Desktop;
