import "./styles/ToMesh.css";
import React from "react";
import { useEffect } from "react";
import { BrowserView, MobileOnlyView } from "react-device-detect";
import Desktop_Title from "modules/toMesh/components/TMIntro";
import Header from "modules/toMesh/components/TMTitle";
import { getMessageToken } from "modules/toMesh/components/firebase-messaging-sw";
import { subscribe } from "diagnostics_channel";
import webpush from "web-push";

const ToMesh = () => {
  function requestPermission() {
    console.log("권한 요청 중...");
    Notification.requestPermission().then((permission) => {
      if (permission === "granted") {
        console.log("알림 권한이 허용됨");
      } else {
        console.log("알림 권한 허용 안됨");
      }
    });
  }

  function initServiceWorker() {
    if ("serviceWorker" in navigator) {
      navigator.serviceWorker
        .register("firebase-messaging-sw.js")
        .then(function (registration) {
          console.log("Registration successful, scope is:", registration.scope);

          return registration;
        })
        .catch(function (err) {
          console.log("Service worker registration failed, error:", err);
        });
    }
  }

  async function handleAllowNotification() {
    const permission = await requestPermission();
    initServiceWorker();
  }

  handleAllowNotification();

  // useEffect(() => {
  //   getMessageToken();
  // }, []);

  return (
    <>
      <Header></Header>
      <BrowserView>
        <h1>
          <Desktop_Title />
        </h1>
      </BrowserView>
      <MobileOnlyView>
        <h1>
          <Desktop_Title />
        </h1>
      </MobileOnlyView>
    </>
  );
};

export default ToMesh;
