import { initializeApp } from "firebase/app";
import { getMessaging, getToken, onMessage } from "firebase/messaging";

const firebaseConfig = {
  apiKey: "AIzaSyBn9tebNSE_zHtdF9XeHpp-NF8fh1Yp-Ys",
  authDomain: "notice-test-faa28.firebaseapp.com",
  projectId: "notice-test-faa28",
  storageBucket: "notice-test-faa28.appspot.com",
  messagingSenderId: "926284303091",
  appId: "1:926284303091:web:98b5e02104f116b2b94586",
  measurementId: "G-DVET9T6B55",
};

const app = initializeApp(firebaseConfig);
const messaging = getMessaging(app);

function requestPermission() {
  console.log("권한 요청 중...");
  Notification.requestPermission().then((permission) => {
    if (permission === "granted") {
      console.log("알림 권한이 허용됨");

      // FCM 메세지 처리
    } else {
      console.log("알림 권한 허용 안됨");
    }
  });
}

requestPermission();

export const onMessageListener = () =>
  new Promise((resolve) => {
    onMessage(messaging, (payload) => {
      resolve(payload);
    });
  });
