import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore/lite";

const firebaseConfig = {
  apiKey: "AIzaSyBn9tebNSE_zHtdF9XeHpp-NF8fh1Yp-Ys",
  authDomain: "notice-test-faa28.firebaseapp.com",
  projectId: "notice-test-faa28",
  storageBucket: "notice-test-faa28.appspot.com",
  messagingSenderId: "926284303091",
  appId: "1:926284303091:web:98b5e02104f116b2b94586",
  measurementId: "G-DVET9T6B55",
};

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);

// Get a list of cities from your database
async function getCities(db) {
  const citiesCol = collection(db, "cities");
  const citySnapshot = await getDocs(citiesCol);
  const cityList = citySnapshot.docs.map((doc) => doc.data());
  return cityList;
}
