import { useState, useEffect } from "react";
import banner_image from "../../../assets/images/Banner.png";
import Desktop_Booth from "modules/toMesh/components/tmUpload";

function Banner(props) {
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });
  return (
    <header>
      <h1>
        <img
          src={banner_image}
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            height: "100%",
            width: innerWidth * 0.8,
          }}
          alt="Title"
          onClick={() => {}}
        />
      </h1>
    </header>
  );
}

function Desktop_Title() {
  const [page, setPage] = useState("TITLE");
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });

  const title_page = (
    <div style={{ display: "flex" }}>
      <div
        style={{
          width: innerWidth,
          color: "white",
          fontSize: innerWidth / 10,
        }}
      >
        Take your photo into 3D Mesh
        <div
          style={{
            width: innerWidth,
            color: "gray",
            fontSize: innerWidth / 25,
          }}
        >
          We build the AI model to generate a high-quality 3D model from a
          single picture within 3 minutes. You can try our model in this demo
          webpage with minimum effort. Don't hesitate it.
        </div>
        <input
          type="button"
          value="Start"
          style={{
            inlineSize: innerWidth,
            fontSize: 40,
          }}
          onClick={() => {
            setPage("UPLOAD");
          }}
        />
        <Banner />
      </div>
    </div>
  );

  let content = null;
  const booth_page = <div />;

  if (page === "TITLE") {
    content = title_page;
  } else if (page === "UPLOAD") {
    content = <Desktop_Booth />;
  }

  return <div className="App">{content} </div>;
}

export default Desktop_Title;
