import { initializeApp } from "firebase/app";
import { getMessaging, getToken, onMessage } from "firebase/messaging";

const firebaseConfig = {
  apiKey: "AIzaSyBn9tebNSE_zHtdF9XeHpp-NF8fh1Yp-Ys",
  authDomain: "notice-test-faa28.firebaseapp.com",
  projectId: "notice-test-faa28",
  storageBucket: "notice-test-faa28.appspot.com",
  messagingSenderId: "926284303091",
  appId: "1:926284303091:web:98b5e02104f116b2b94586",
  measurementId: "G-DVET9T6B55",
};

const app = initializeApp(firebaseConfig);

const messaging = getMessaging(app);

export const requestPermission = () => {
  console.log("권한 요청 중...");
  Notification.requestPermission().then((permission) => {
    if (permission === "granted") {
      console.log("알림 권한이 허용됨");
      return getToken(messaging, {
        vapidKey:
          "BC5frPVyX5VqaUU-pwEbevfYjqBMymWollYD1ImZ4BTPbqsFnyN64dLCqwLAWtYjQ7b2DVS5yGLbVelgEIfdDyU",
      })
        .then((currentToken) => {
          if (currentToken) {
            console.log("Client Token: ", currentToken);
            // Send the token to your server and update the UI if necessary
            // ...
          } else {
            // Show permission request UI
            console.log(
              "No registration token available. Request permission to generate one."
            );
            // ...
          }
        })
        .catch((e) => console.log("error==> ", e));
    } else {
      console.log("알림 권한 허용 안됨");
    }
  });
};

requestPermission();

export const onMessageListener = () =>
  new Promise((resolve) => {
    onMessage(messaging, (payload) => {
      resolve(payload);
    });
  });
