import { useState, Imag } from "react";
import logo_image from "../../../assets/images/Mingle_AI_BI_v1_20231114_black.png";

function Header(props) {
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });

  return (
    <header>
      <h1>
        <img
          src={logo_image}
          alt="Title"
          style={{ width: 100 + innerWidth }}
          onClick={() => {}}
        />
      </h1>
    </header>
  );
}

function Nav(props) {
  const list = [];

  for (let i = 0; i < props.topics.length; i++) {
    let t = props.topics[i];
    list.push(
      <li key={t.id}>
        <a
          id={t.id}
          href={"/read/" + t.id}
          onClick={(event) => {
            event.preventDefault();
            props.onChangeMode(event.target.id);
          }}
        >
          {t.title}
        </a>
      </li>
    );
  }
  return (
    <nav>
      <ol>{list}</ol>
    </nav>
  );
}

function Article(props) {
  return (
    <article>
      <h2>{props.title}</h2>
      {props.message}
    </article>
  );
}

function Launch_Desktop() {
  const [mode, setMode] = useState("WELCOME");
  const [id, setID] = useState(null);
  const [nextId, setNextId] = useState(4);
  const [topics, setTopics] = useState([
    { id: 1, title: "html", body: "html is ..." },
    { id: 2, title: "css", body: "css is ..." },
    { id: 3, title: "java script", body: "java script is ..." },
  ]);

  let content = null;
  let contextControl = null;

  if (mode === "WELCOME") {
    content = <Article title="Welcome" message="Hello, how are you?"></Article>;
  } else if (mode === "READ") {
    content = (
      <Article
        title={topics[id - 1].title}
        message={topics[id - 1].body}
      ></Article>
    );
    contextControl = (
      <>
        <li>
          <a
            href={"/update/" + id}
            onClick={(event) => {
              event.preventDefault();
              setMode("UPDATE");
            }}
          >
            Update
          </a>
        </li>
        <li>
          <input
            type="button"
            value="Delete"
            onClick={() => {
              const newTopics = [];
              for (let i = 0; i < topics.length; i++) {
                if (topics[i].id !== id) {
                  newTopics.push(topics[i]);
                }
              }
              setTopics(newTopics);
            }}
          />
        </li>
      </>
    );
  }
  return (
    <div className="App">
      <Header
        title="Title"
        onChangeMode={() => {
          setMode("WELCOME");
        }}
      ></Header>
      <Nav
        topics={topics}
        onChangeMode={(id) => {
          setMode("READ");
          setID(id);
        }}
      ></Nav>
      {content}

      {id}
    </div>
  );
}

export default Launch_Desktop;
