import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
import { getToken, getMessaging } from "firebase/messaging";

const firebaseConfig = {
  apiKey: "AIzaSyBn9tebNSE_zHtdF9XeHpp-NF8fh1Yp-Ys",
  authDomain: "notice-test-faa28.firebaseapp.com",
  projectId: "notice-test-faa28",
  storageBucket: "notice-test-faa28.appspot.com",
  messagingSenderId: "926284303091",
  appId: "1:926284303091:web:98b5e02104f116b2b94586",
  measurementId: "G-DVET9T6B55",
};

function requestPermission() {
  console.log("권한 요청 중...");
  Notification.requestPermission().then((permission) => {
    if (permission === "granted") {
      console.log("알림 권한이 허용됨");

      // FCM 메세지 처리
    } else {
      console.log("알림 권한 허용 안됨");
    }
  });
}

const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);

requestPermission();

export const askForNotification = async () => {
  const tempMessaging = await messaging();

  if (tempMessaging) {
    const newSw = await navigator.serviceWorker.register(
      "/homepage/basename/firebase-messaging-sw.js"
    );

    return getNotificationPermission(
      async () =>
        await getToken(tempMessaging, {
          vapidKey,
          serviceWorkerRegistration: newSw,
        })
    );
  }
};
