import { useState, ImgArea, Img, ImgName, DeleteButton } from "react";
import React from "react";
import "./desktopBooth.css";

function Desktop_Booth() {
  const [img, setImg] = useState(null);
  const [previewImg, setPreviewImg] = useState(null);

  const insertImg = (e) => {
    let reader = new FileReader();

    if (e.target.files[0]) {
      reader.readAsDataURL(e.target.files[0]);

      setImg(e.target.files[0]);
    }

    reader.onloadend = () => {
      const previewImgUrl = reader.result;

      if (previewImgUrl) {
        setPreviewImg([...previewImg, previewImgUrl]);
      }
    };
  };

  const deleteImg = () => {
    setImg(null);
    setPreviewImg(null);
  };

  return (
    <div>
      <ImgArea>
        <Img src={previewImg} alt="img" />
      </ImgArea>
      <ImgName>{img}</ImgName>
      <DeleteButton onClick={deleteImg}>‚ùå</DeleteButton>
    </div>
  );
}

export default Desktop_Booth;
