import "./styles/ToMesh.css";
import React from "react";
import { useState } from "react";
import { BrowserView, MobileOnlyView } from "react-device-detect";
import Desktop_Title from "modules/toMesh/components/TMIntro";
import Header from "modules/toMesh/components/TMTitle";
import {} from "modules/toMesh/components/firebase-messaging-sw";

function requestPermission() {
  console.log("권한 요청 중...");
  Notification.requestPermission().then((permission) => {
    if (permission === "granted") {
      console.log("알림 권한이 허용됨");

      // FCM 메세지 처리
    } else {
      console.log("알림 권한 허용 안됨");
    }
  });
}

const ToMesh = () => {
  const [deviceToken, setDeviceToken] = useState({
    token: "",
  });
  if ("serviceWorker" in navigator) {
    navigator.serviceWorker.register("firebase-message-sw.js");
  }

  requestPermission();

  navigator.serviceWorker
    .register("firebase-message-sw.js")
    .then(function (registration) {
      console.log("Service Worker 등록 성공:", registration);
    })
    .catch(function (error) {
      console.log("Service Worker 등록 실패:", error);
    });

  return (
    <>
      <Header></Header>
      <BrowserView>
        <h1>
          <Desktop_Title />
        </h1>
      </BrowserView>
      <MobileOnlyView>
        <h1>
          <Desktop_Title />
        </h1>
      </MobileOnlyView>
    </>
  );
};

export default ToMesh;
