import { useState } from "react";
import React from "react";
import "./desktopBooth.css";

function Desktop_Booth() {
  const [myImage, setMyImage] = useState([]);
  const addImage = (e) => {
    const nowSelectImageList = e.target.files;
    //한번에 받은 파일리스트 (object)
    const nowImageURLList = [...myImage]; // 현재 myImage 복사하고
    for (let i = 0; i < nowSelectImageList.length; i += 1) {
      //nowselectImageList object를 1를 이용해서 돌리면서
      const nowImageUrl = URL.createObjectURL(nowSelectImageList[i]);
      //미리보기 가능하게 변수화
      nowImageURLList.push(nowImageUrl);
      //복사한 myImage에 추가
    }
    setMyImage(nowImageURLList);
    //MyImage원본에 덮어씌우기
  };
  for (let i = 0; i < myImage.length; i++) {}
  return (
    <div
      style={{
        color: "white",
      }}
    >
      <label
        htmlFor="input-file"
        className="OOTDWrite-input-file"
        onChange={addImage}
      >
        Add your photo{" "}
        <input
          type="file"
          multiple="multiple"
          id="input-file"
          style={{ display: "none" }}
          accept=".jpg, .jpeg, .png"
        />
      </label>
      <div className="preview">
        {myImage.map((image, id) => (
          <div key={id}>
            <img src={image} alt={`${image}-${id}`} />
            <Delete onClick={() => handleDeleteImage(id)} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Desktop_Booth;
