import { useState, useEffect } from "react";
import logo_image from "../../../assets/images/Mingle_AI_BI_v1_20231114_white.png";
import banner_image from "../../../assets/images/Banner.png";

function Header(props) {
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });

  return (
    <header>
      <h1>
        <img
          src={logo_image}
          alt="Title"
          style={{ width: 50 + innerWidth / 10 }}
          onClick={() => {}}
        />
      </h1>
    </header>
  );
}

function Banner(props) {
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });
  return (
    <header>
      <h1>
        <img
          src={banner_image}
          style={{ width: innerWidth / 2 }}
          alt="Title"
          onClick={() => {}}
        />
      </h1>
    </header>
  );
}

function TryButton(props) {
  return (
    <input
      type="button"
      value="Try"
      style={{
        inlineSize: props.innerWidth / 2 - 50,
        fontSize: 40,
      }}
      onClick={() => {}}
    />
  );
}

function Desktop_Title() {
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });

  return (
    <body bgcolor="black">
      <div className="App">
        <Header
          title="Title"
          onChangeMode={() => {
            setMode("WELCOME");
          }}
        ></Header>
        <div style={{ display: "flex" }}>
          <div style={{ width: innerWidth / 2 }}>
            <Banner></Banner>
          </div>
          <div
            style={{
              width: innerWidth / 2,
              color: "white",
              fontSize: innerWidth / 30,
            }}
          >
            Take a photo to create different styles of avatars!
            <div
              style={{
                width: innerWidth / 2,
                color: "gray",
                fontSize: innerWidth / 40,
              }}
            >
              Photos taken are completely discarded after the avatar is created.
            </div>
            <TryButton innerWidth={innerWidth}></TryButton>
          </div>
        </div>
      </div>
    </body>
  );
}

export default Desktop_Title;
