import { React, useState, useEffect } from "react";
import banner_image from "../../../assets/images/Banner.png";
import Desktop_Booth from "modules/toMesh/components/tmUpload";

function Banner(props) {
  return (
    <header>
      <h1>
        <img
          src={banner_image}
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
          }}
          alt="Title"
        />
      </h1>
    </header>
  );
}

function Title(props) {
  return (
    <div
      style={{
        width: "100%",
        color: "white",
        fontSize: 36,
        margin: 20,
      }}
    ></div>
  );
}

function Description(props) {
  return (
    <div
      style={{
        width: "100%",
        color: "gray",
        fontSize: 14,
      }}
    >
      {props.val}
    </div>
  );
}

function Desktop_Title() {
  const [page, setPage] = useState("TITLE");

  const title_page = (
    <>
      <Title val="Take your photo into 3D Mesh" />
      <input
        type="button"
        value="Start"
        style={{
          inlineSize: "100%",
          fontSize: 36,
          marginTop: 30,
        }}
        onClick={() => {
          setPage("UPLOAD");
        }}
      />
      <Banner />
    </>
  );

  let content = null;
  const booth_page = <div />;

  if (page === "TITLE") {
    content = title_page;
  } else if (page === "UPLOAD") {
    content = <Desktop_Booth />;
  }

  return <div className="App">{content} </div>;
}

export default Desktop_Title;
