import { useState } from "react";
import React from "react";
import "./tmUpload.css";
import logo_image from "../../../assets/images/Upload.png";

function Desktop_Booth() {
  let content = null;
  const [selectedID, setSelectedId] = useState(null);
  const [page, setPage] = useState("SELECTION");
  const [myImage, setMyImage] = useState([]);

  const addImage = (e) => {
    const nowSelectImageList = e.target.files;
    const nowImageURLList = [];

    for (let i = 0; i < nowSelectImageList.length; i += 1) {
      const nowImageUrl = URL.createObjectURL(nowSelectImageList[i]);
      nowImageURLList.push(nowImageUrl);
    }

    setMyImage(nowImageURLList);
    setPage("SELECTED");
  };

  const handleDeleteImage = (id) => {
    setMyImage(myImage.filter((_, index) => index !== id));
  };

  const selection = (
    <>
      <div
        style={{
          width: "100%",
          color: "white",
        }}
      >
        <div
          style={{
            color: "white",
            fontSize: 36,
            margin: 20,
          }}
        >
          Upload just a single picture{" "}
        </div>
        <div
          style={{
            color: "gray",
            fontSize: 14,
            margin: 20,
          }}
        >
          Your photo will be discarded after 3D model generation safely. We do
          not collect your information identifiable.
        </div>
      </div>
      <div
        style={{
          color: "white",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <label
          htmlFor="input-file"
          onChange={addImage}
          style={{
            color: "white",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <img
            src={logo_image}
            style={{
              width: "90%",
            }}
            alt="Title"
            onClick={() => {}}
          />

          <input
            type="file"
            multiple="multiple"
            id="input-file"
            style={{ display: "none" }}
            accept=".jpg, .jpeg, .png"
          />
        </label>
      </div>
    </>
  );

  const selected = (
    <div className="preview" style={{}}>
      <div
        style={{
          width: "100%",
          color: "white",
        }}
      >
        <div
          style={{
            color: "white",
            fontSize: 35,
            margin: 20,
          }}
        >
          Awesome photo!
        </div>
        <div
          style={{
            width: "80%",
            color: "gray",
            fontSize: 15,
            margin: 20,
          }}
        >
          Your photo will be discarded after 3D model generation safely. We do
          not collect your information identifiable.
        </div>
      </div>
      <div
        onClick={() => {
          if (selectedID !== null) {
            setSelectedId(null);
          }
        }}
      >
        {myImage.map((image, id) => (
          <>
            <img
              src={image}
              alt={`${image}-${id}`}
              style={{
                margin: "2%",
                width: myImage.length <= 1 ? "95%" : "27%",
                border: "2px solid skyblue",
              }}
              onClick={(event) => {
                if (selectedID !== id) {
                  event.stopPropagation();
                  setSelectedId(id);
                }
              }}
            />
            <input
              type="button"
              value="X"
              style={{
                width: 50,
                height: 50,
                position: "absolute",
                marginTop: "2%",
                marginLeft: -50,
                display: id === selectedID ? "" : "none",
              }}
              onClick={() => {
                handleDeleteImage(id);
              }}
            />
          </>
        ))}
        <div>
          <input
            type="button"
            value="Reset"
            style={{
              width: "100%",
              height: 50,
              position: "fixed",
              bottom: 50,
            }}
            onClick={() => {
              setPage("SELECTION");
            }}
          />
        </div>
      </div>
    </div>
  );

  if (page === "SELECTION" || myImage.length === 0) {
    content = selection;
  } else if (page === "SELECTED") {
    content = selected;
  }

  return <>{content}</>;
}

export default Desktop_Booth;
