import { wait } from "@testing-library/user-event/dist/utils";
import { useState, useEffect } from "react";
import { SquareLoader } from "react-spinners";

function Viewer() {
  const [waiting_text, setWaitingText] = useState("Waiting");
  const [page, setPage] = useState("WAITING");

  function MeshViewer() {}
  function Waiting() {
    return (
      <div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "45%",
          }}
        >
          <SquareLoader size={100} color="aquamarine" />
        </div>
        <input
          type="button"
          value="완성되면 알려줄까?"
          style={{
            width: "80%",
            height: 70,
            position: "fixed",
            bottom: 0,
            margin: "10%",
            borderRadius: 30,
            backgroundColor: "yellowgreen",
            fontSize: 24,
            color: "black",
          }}
          onClick={() => {
            alert("푸시 알림 미구현 상태 (서버 통합 후 적용 예정)");
          }}
        />
      </div>
    );
  }

  const button_notification = (
    <div>{page === "WAITING" ? <Waiting /> : <></>}</div>
  );
  return button_notification;
}

export default Viewer;
