import FireBase from "firebase";
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
import { getMessaging, getToken } from "firebase/messaging";

const firebaseConfig = {
  apiKey: "AIzaSyBn9tebNSE_zHtdF9XeHpp-NF8fh1Yp-Ys",
  authDomain: "notice-test-faa28.firebaseapp.com",
  projectId: "notice-test-faa28",
  storageBucket: "notice-test-faa28.appspot.com",
  messagingSenderId: "926284303091",
  appId: "1:926284303091:web:98b5e02104f116b2b94586",
  measurementId: "G-DVET9T6B55",
};

const [deviceToken, setDeviceToken] = useState<AppCheckTokenResult>({
  token: "",
});

async function handleAllowNotification() {
  const permission = await Notification.requestPermission();
  registerServiceWorker();
}

async function getDeviceToken() {
  const token = await getToken(messaging, {
    vapidKey: import.meta.env.VITE_APP_VAPID_KEY,
  });

  setDeviceToken({
    token: token,
  });
}

export const app = initializeApp(firebaseConfig);
export const messaging = getMessaging(app);
const analytics = getAnalytics(app);
