import { useState, useEffect } from "react";
import React from "react";
import "./tmUpload.css";
import logo_image from "../../../assets/images/Upload.png";

function Desktop_Booth() {
  const [myImage, setMyImage] = useState([]);
  const [innerWidth, setInnerWidth] = useState(window.innerWidth);

  useEffect(() => {
    const resizeListener = () => {
      setInnerWidth(window.innerWidth);
    };
    window.addEventListener("resize", resizeListener);
  });

  const addImage = (e) => {
    const nowSelectImageList = e.target.files;
    const nowImageURLList = [];

    for (let i = 0; i < nowSelectImageList.length; i += 1) {
      const nowImageUrl = URL.createObjectURL(nowSelectImageList[i]);
      nowImageURLList.push(nowImageUrl);
    }

    setMyImage(nowImageURLList);
  };

  const handleDeleteImage = (id) => {
    setMyImage(myImage.filter((_, index) => index !== id));
  };

  return (
    <>
      {" "}
      <div
        style={{
          width: innerWidth,
          color: "white",
          fontSize: innerWidth / 10,
          border: 10,
        }}
      >
        Upload just a single picture
        <div
          style={{
            width: innerWidth,
            color: "gray",
            fontSize: innerWidth / 25,
          }}
        >
          We build the AI model to generate a high-quality 3D model from a
          single picture within 3 minutes. You can try our model in this demo
          webpage with minimum effort. Don't hesitate it.
        </div>
      </div>
      <div
        style={{
          color: "white",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <label htmlFor="input-file" onChange={addImage}>
          <img
            src={logo_image}
            style={{
              width: innerWidth * 0.9,
            }}
            alt="Title"
            onClick={() => {}}
          />

          <input
            type="file"
            multiple="multiple"
            id="input-file"
            style={{ display: "none" }}
            accept=".jpg, .jpeg, .png"
          />
        </label>
        <div className="preview" style={{}}>
          {myImage.map((image, id) => (
            <>
              <img src={image} alt={`${image}-${id}`} style={{ width: 300 }} />
              <input
                type="button"
                value="X"
                style={{ width: 50, height: 50 }}
                onChange={() => handleDeleteImage(id)}
              />
            </>
          ))}
        </div>
      </div>
    </>
  );
}

export default Desktop_Booth;
