import { useState, useEffect } from "react";
import React from "react";
import "./tmUpload.css";
import logo_image from "../../../assets/images/Upload.png";

function Desktop_Booth() {
  let content = null;
  const [page, setPage] = useState("SELECTION");
  const [myImage, setMyImage] = useState([]);

  const addImage = (e) => {
    const nowSelectImageList = e.target.files;
    const nowImageURLList = [];

    for (let i = 0; i < nowSelectImageList.length; i += 1) {
      const nowImageUrl = URL.createObjectURL(nowSelectImageList[i]);
      nowImageURLList.push(nowImageUrl);
    }

    setMyImage(nowImageURLList);
    setPage("SELECTED");
  };

  const selection = (
    <>
      <div
        style={{
          width: "100%",
          color: "white",
        }}
      >
        <div
          style={{
            color: "white",
            fontSize: 35,
            margin: 20,
          }}
        >
          Upload just a single picture{" "}
        </div>
        <div
          style={{
            width: "80%",
            color: "gray",
            fontSize: 15,
            margin: 20,
          }}
        >
          Your photo will be discarded after 3D model generation safely. We do
          not collect your information identifiable.
        </div>
      </div>
      <div
        style={{
          color: "white",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "100%",
        }}
      >
        <label
          htmlFor="input-file"
          onChange={addImage}
          style={{
            color: "white",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <img
            src={logo_image}
            style={{
              width: "90%",
            }}
            alt="Title"
            onClick={() => {}}
          />

          <input
            type="file"
            multiple="multiple"
            id="input-file"
            style={{ display: "none" }}
            accept=".jpg, .jpeg, .png"
          />
        </label>
      </div>
    </>
  );

  const selected = (
    <div className="preview" style={{}}>
      {myImage.map((image, id) => (
        <>
          <img src={image} alt={`${image}-${id}`} style={{ width: "33%" }} />
          {/* <input
            type="button"
            value="X"
            style={{ width: 50, height: 50 }}
            onChange={() => handleDeleteImage(id)}
          /> */}
        </>
      ))}
      <div>
        <input
          type="button"
          value="Reset"
          style={{ width: "100%", height: 50, marginTop: 50 }}
          onChange={() => {
            setPage("SELECTION");
          }}
        />
      </div>
    </div>
  );

  if (page === "SELECTION") {
    content = selection;
  } else if (page === "SELECTED") {
    content = selected;
  }

  const handleDeleteImage = (id) => {
    setMyImage(myImage.filter((_, index) => index !== id));
  };

  return <>{content}</>;
}

export default Desktop_Booth;
